settings.cardCss:
@media only screen and (min-width: 1200px){
    .card {
       width: 40%;
       height: 100%;
       box-sizing: border-box;
       margin: auto;
    }
    .content {
       padding: 20px;
       display: flex;
       flex-direction: column;
       justify-content: space-around;
       height: 100%;
       box-sizing: border-box;
    }
    .title-desc{
        display: flex;
        font-weight: bold;
    }
    i{
        margin-right: 10px;
        color: #3363AD;
        font-size: 24px;
    }
    .label-name{
        color: #3363AD;
    }
    .set-value{
        margin-top: 15px;
        width: 130px;
        display: flex;
        justify-content: center;
        height: 35px;
        box-shadow: rgba(0, 0, 0, 0.06) 0px 2px 4px 0px inset;
        border-radius: 30px;
    }

    .value{
        display: flex;
        flex-direction: column;
        justify-content: center;
        font-weight: bold;
        color: #6E6E6E;
    }
}

