settings.cardCss:
@media only screen and (min-width: 1200px){
    .card {
        font-weight: bold;
        font-size: 32px;
        color: #999;
        width: 100%;
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
    } 
}


@media only screen and (max-width: 1200px){
    .card {
        font-weight: bold;
        font-size: 32px;
        color: #999;
        width: 100%;
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
    } 
}
