        font-weight: bold;
        font-size: 29px;
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        width: 80%;
        color: #6E6E6E;
        margin: auto;

    }
    .wrap{
        display: flex;
        flex-direction: column;
        justify-content: center;
        /*border: 1px solid #0F563D;*/
